/* eslint-disable @typescript-eslint/no-explicit-any */
declare module '*.png' {
  const content: any;
  // eslint-disable-next-line import/no-default-export
  export default content;
}

declare module '*.svg' {
  const content: any;
  // eslint-disable-next-line import/no-default-export
  export default content;
}
